{
  "Comment": "Workflow automatizado para procesamiento de pedidos",
  "StartAt": "ConfirmOrder",
  "States": {
    "ConfirmOrder": {
      "Type": "Task",
      "Resource": "${ConfirmOrderLambdaFunction.Arn}",
      "ResultPath": "$.confirmResult",
      "TimeoutSeconds": 30,
      "Retry": [
        {
          "ErrorEquals": ["States.TaskFailed", "States.Timeout"],
          "IntervalSeconds": 2,
          "MaxAttempts": 3,
          "BackoffRate": 2.0
        }
      ],
      "Catch": [
        {
          "ErrorEquals": ["States.ALL"],
          "ResultPath": "$.error",
          "Next": "OrderFailed"
        }
      ],
      "Next": "AssignCook"
    },
    "AssignCook": {
      "Type": "Task",
      "Resource": "${AssignCookLambdaFunction.Arn}",
      "ResultPath": "$.cookResult",
      "TimeoutSeconds": 30,
      "Retry": [
        {
          "ErrorEquals": ["States.TaskFailed", "States.Timeout"],
          "IntervalSeconds": 2,
          "MaxAttempts": 3,
          "BackoffRate": 2.0
        }
      ],
      "Catch": [
        {
          "ErrorEquals": ["States.ALL"],
          "ResultPath": "$.error",
          "Next": "OrderFailed"
        }
      ],
      "Next": "WaitForCooking"
    },
    "WaitForCooking": {
      "Type": "Wait",
      "Seconds": 300,
      "Comment": "Espera simulada de coccin (5 min)",
      "Next": "CompleteCooking"
    },
    "CompleteCooking": {
      "Type": "Task",
      "Resource": "${CompleteCookingLambdaFunction.Arn}",
      "ResultPath": "$.cookingResult",
      "TimeoutSeconds": 30,
      "Retry": [
        {
          "ErrorEquals": ["States.TaskFailed", "States.Timeout"],
          "IntervalSeconds": 2,
          "MaxAttempts": 3,
          "BackoffRate": 2.0
        }
      ],
      "Catch": [
        {
          "ErrorEquals": ["States.ALL"],
          "ResultPath": "$.error",
          "Next": "OrderFailed"
        }
      ],
      "Next": "WaitForPacking"
    },
    "WaitForPacking": {
      "Type": "Wait",
      "Seconds": 120,
      "Comment": "Espera simulada de empaquetado (2 min)",
      "Next": "AssignDriver"
    },
    "AssignDriver": {
      "Type": "Task",
      "Resource": "${AssignDriverLambdaFunction.Arn}",
      "ResultPath": "$.driverResult",
      "TimeoutSeconds": 30,
      "Retry": [
        {
          "ErrorEquals": ["States.TaskFailed", "States.Timeout"],
          "IntervalSeconds": 2,
          "MaxAttempts": 3,
          "BackoffRate": 2.0
        }
      ],
      "Catch": [
        {
          "ErrorEquals": ["States.ALL"],
          "ResultPath": "$.error",
          "Next": "OrderFailed"
        }
      ],
      "Next": "WaitForDelivery"
    },
    "WaitForDelivery": {
      "Type": "Wait",
      "Seconds": 600,
      "Comment": "Espera simulada de entrega (10 min)",
      "Next": "CompleteDelivery"
    },
    "CompleteDelivery": {
      "Type": "Task",
      "Resource": "${CompleteDeliveryLambdaFunction.Arn}",
      "ResultPath": "$.deliveryResult",
      "TimeoutSeconds": 30,
      "Retry": [
        {
          "ErrorEquals": ["States.TaskFailed", "States.Timeout"],
          "IntervalSeconds": 2,
          "MaxAttempts": 3,
          "BackoffRate": 2.0
        }
      ],
      "Catch": [
        {
          "ErrorEquals": ["States.ALL"],
          "ResultPath": "$.error",
          "Next": "OrderFailed"
        }
      ],
      "End": true
    },
    "OrderFailed": {
      "Type": "Task",
      "Resource": "${HandleOrderFailureLambdaFunction.Arn}",
      "ResultPath": "$.failureResult",
      "End": true
    }
  }
}
