{
  "Comment": "Workflow automatizado hasta ready - Driver contina manualmente",
  "StartAt": "ConfirmOrder",
  "States": {
    "ConfirmOrder": {
      "Type": "Task",
      "Resource": "${ConfirmOrderLambdaFunction.Arn}",
      "ResultPath": "$.confirmResult",
      "TimeoutSeconds": 30,
      "Retry": [
        {
          "ErrorEquals": ["States.TaskFailed", "States.Timeout"],
          "IntervalSeconds": 2,
          "MaxAttempts": 3,
          "BackoffRate": 2.0
        }
      ],
      "Catch": [
        {
          "ErrorEquals": ["States.ALL"],
          "ResultPath": "$.error",
          "Next": "OrderFailed"
        }
      ],
      "Next": "AssignCook"
    },
    "AssignCook": {
      "Type": "Task",
      "Resource": "${AssignCookLambdaFunction.Arn}",
      "ResultPath": "$.cookResult",
      "TimeoutSeconds": 30,
      "Retry": [
        {
          "ErrorEquals": ["States.TaskFailed", "States.Timeout"],
          "IntervalSeconds": 2,
          "MaxAttempts": 3,
          "BackoffRate": 2.0
        }
      ],
      "Catch": [
        {
          "ErrorEquals": ["States.ALL"],
          "ResultPath": "$.error",
          "Next": "OrderFailed"
        }
      ],
      "Next": "WaitForCooking"
    },
    "WaitForCooking": {
      "Type": "Wait",
      "Seconds": 300,
      "Comment": "Espera simulada de coccin (5 min)",
      "Next": "CompleteCooking"
    },
    "CompleteCooking": {
      "Type": "Task",
      "Resource": "${CompleteCookingLambdaFunction.Arn}",
      "ResultPath": "$.cookingResult",
      "TimeoutSeconds": 30,
      "Retry": [
        {
          "ErrorEquals": ["States.TaskFailed", "States.Timeout"],
          "IntervalSeconds": 2,
          "MaxAttempts": 3,
          "BackoffRate": 2.0
        }
      ],
      "Catch": [
        {
          "ErrorEquals": ["States.ALL"],
          "ResultPath": "$.error",
          "Next": "OrderFailed"
        }
      ],
      "Next": "WaitForDriverPickup"
    },
    "WaitForDriverPickup": {
      "Type": "Wait",
      "Seconds": 7200,
      "Comment": "Espera 2 horas a que un driver tome el pedido manualmente. Pedido queda en estado 'ready' disponible para drivers.",
      "Next": "WorkflowCompleted"
    },
    "WorkflowCompleted": {
      "Type": "Succeed",
      "Comment": "Workflow automtico completado en estado 'ready'. Driver debe tomar el pedido usando POST /driver/pickup/{order_id} y completar usando POST /driver/complete/{order_id}"
    },
    "OrderFailed": {
      "Type": "Task",
      "Resource": "${HandleOrderFailureLambdaFunction.Arn}",
      "ResultPath": "$.failureResult",
      "End": true
    }
  }
}

